import React, { useEffect } from "react";
import right from "../assets/right_img.png";
import arrow from "../assets/arrow_icon.png";
import './Mailit.css';

export const Mailit = () => {
  useEffect(() => {
    const contactForm = document.querySelector('.contact-left');
    const sections = document.querySelectorAll('section');

    function checkVisibility() {
      const windowHeight = window.innerHeight;
      const scrollY = window.scrollY;

      // Check contact form visibility
      const formTop = contactForm.getBoundingClientRect().top + scrollY;
      if (scrollY + windowHeight > formTop + 100) {
        contactForm.classList.add('visible');
      }

      // Check sections visibility
      sections.forEach((section, index) => {
        const sectionTop = section.getBoundingClientRect().top + scrollY;

        if (scrollY + windowHeight > sectionTop + 100) {
          section.classList.add('visible');
          if (index > 0) {
            sections[index - 1].classList.add('past');
          }
        }
      });
    }

    window.addEventListener('scroll', checkVisibility);
    checkVisibility();

    return () => {
      window.removeEventListener('scroll', checkVisibility);
    };
  }, []);

  return (
    <div className="contact-container">
      <form action="https://api.web3forms.com/submit" method="POST" className="contact-left">
        <div className="contact-left-title">
          <h2>Get in Touch</h2>
          <hr />
        </div>
        <input type="hidden" name="access_key" value="09b9644d-7053-45c3-9a62-08017dcab12f" />
        <input type="text" name="name" placeholder="Your Name" className="contact-inputs" required />
        <input type="email" name="email" placeholder="Your Email" className="contact-inputs" required />
        <textarea name="message" placeholder="Your Message" className="contact-inputs" required></textarea>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

// Dummy sections to demonstrate the scroll effect
export const DummySections = () => {
  return (
    <>
      <section>Content for Section 1</section>
      <section>Content for Section 2</section>
      <section>Content for Section 3</section>
    </>
  );
};
