import React, { useEffect } from 'react';
import './App.css';
import { NavBar } from './components/Navbar';
import { Banner } from './components/Banner';
import { Skills } from './components/Skills';
import { Projects } from './components/Projects';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Mailit } from './components/Mailit';

function App() {
  useEffect(() => {
    const sections = document.querySelectorAll('section');

    function checkVisibility() {
      const windowHeight = window.innerHeight;
      const scrollY = window.scrollY;

      // Check sections visibility
      sections.forEach((section, index) => {
        const sectionTop = section.getBoundingClientRect().top + scrollY;

        if (scrollY + windowHeight > sectionTop + 100) {
          section.classList.add('visible');
          if (index > 0) {
            sections[index - 1].classList.add('past');
          }
        }
      });
    }

    window.addEventListener('scroll', checkVisibility);
    checkVisibility();

    return () => {
      window.removeEventListener('scroll', checkVisibility);
    };
  }, []);

  return (
    <div className="App">
      <NavBar />
      <Banner />
      <Skills />
      <Projects />
      <Mailit />
      {/* Dummy sections for demonstration purposes */}
      <section className="content-section">Content for Section 1</section>
      <section className="content-section">Content for Section 2</section>
      <section className="content-section">Content for Section 3</section>
    </div>
  );
}

export default App;
